import React, { useEffect, useState } from 'react'
import * as authors from '../../data/authors.json'
import * as books from '../../data/books.json'
import * as magazines from '../../data/magazines.json'
import { useStore } from 'react-redux'

const LibraryTable = () => {

  const store = useStore();
  const [libraryData, setLibraryData] = useState([]);

  useEffect(() => {
    console.log('store', store.getState().library);
    setLibraryData(store.getState().library?.fullData);
  }, [store.getState().library?.fullData]);

  const makeAuthorsCell = (authorsMails) => {
    return (<div style={{display: 'inline-block'}}>
      {authorsMails.split(',').map((item) => {
        let author = {};
        for (let i = 0; i < authors.default.length; i++) {
          if (authors.default[i].email === item) {
            author = authors.default[i];
            break;
          }
        }

        return (
          <>
            <div>
              {author.firstname} {author.lastname}
            </div>
          </>
        );
      })}
    </div>);
  }

  const makeTableRows = () => {
    return (
      <div>
        {libraryData?.map((item, index) => {
          return (
            <div key={`library-table-row-${index}`} style={{width: '100%', borderBottom: '1px solid', display: 'flex', alignItems: 'center', padding: '8px 0'}}>
              <div style={{display: 'inline-block', width: '15%'}}>
                {item.isbn}
              </div>
              <div style={{display: 'inline-block', width: '15%'}}>
                {item.title}
              </div>
              <div style={{display: 'inline-block', width: '40%'}}>
                {item.additionalData.value}
              </div>
              {/*{makeAuthorsCell(item.authors)}*/}
            </div>
          );
        })}
      </div>)
  }

  return (
    <div>
      Library
      {makeTableRows()}
    </div>
  );
}

export default LibraryTable;
