import React, { useEffect } from 'react';
import * as authors from './../data/authors.json';
import * as books from './../data/books.json';
import * as magazines from './../data/magazines.json';

export default () => {

  useEffect(() => {
  }, []);

  const makeAuthorsCell = (authorsMails) => {
    return (<div style={{display: 'inline-block'}}>
      {authorsMails.split(',').forEach((item) => {
        debugger;
        let author = {};
        for (let i = 0; i < authors.default.length; i++) {
          if (authors.default[i].email === item) {
            author = 'hello';//authors.default[i];
            break;
          }
        }

        return (
          <>
            <div>
              {author}
            </div>
          </>
        );
      })}
    </div>);
  }

  const makeTableRows = () => {
    return (
      <div>
        {[].concat(books.default, magazines.default).map((item, index) => {
          return (
            <div key={`library-table-row-${index}`} style={{width: '100%', borderBottom: '1px solid', display: 'flex', alignItems: 'center', padding: '8px 0'}}>
              <div style={{display: 'inline-block', width: '10%'}}>
                {item.isbn}
              </div>
              <div style={{display: 'inline-block', width: '10%'}}>
                {item.title}
              </div>
              <div style={{display: 'inline-block', width: '30%'}}>
                {item.description || item.publishedAt}
              </div>
              {makeAuthorsCell(item.authors)}
            </div>
          );
        })}
      </div>)
  }

  return (
    <div>
      Library
      {makeTableRows()}
    </div>
  );
}
