import React, { useState } from 'react'
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Switch,
  FormControlLabel,
  Button, TextField
} from '@material-ui/core'
import { useDispatch } from 'react-redux'
import { BOOK, MAGAZINE, addBookMagazineTextFields } from './config'

const AddBookMagazineDialog = ({ openState = false, onCloseCallback }) => {
  const [additionType, setAdditionType] = useState(BOOK)
  const [newBookMagazineData, setNewBookMagazineData] = useState({
    title: '',
    isbn: '',
    authors: '',
    publishedAt: '',
    description: ''
  })

  const validationSuccess = () => (newBookMagazineData.title && newBookMagazineData.isbn && newBookMagazineData.authors)

  return (
    <Dialog open={openState} onClose={onCloseCallback}>
      <DialogTitle>Add new</DialogTitle>
      <DialogContent>
        <FormControlLabel
          control={
            <Switch
              onChange={() => {
                const type = additionType === BOOK ? MAGAZINE : BOOK
                setAdditionType(type)
                setNewBookMagazineData({
                  title: '',
                  isbn: '',
                  authors: '',
                  publishedAt: '',
                  description: ''
                })
              }}
              color='primary'
            />}
          label={additionType}
        />
        <br />
        {addBookMagazineTextFields.map((item, index) => (
          <TextField
            key={`add-book-magazine-text-field-${index}`}
            onChange={(e) => {
              setNewBookMagazineData({ ...newBookMagazineData, [item.key]: e.target.value })
            }}
            value={newBookMagazineData[item.key]}
            placeholder={item.placeholder}
            style={{ width: '500px', marginBottom: '16px' }}
          />
        ))}
      </DialogContent>
      <DialogActions>
        <Button
          onClick={onCloseCallback}
        >
          Cancel
        </Button>
        <Button
          onClick={() => {
            validationSuccess() && (
              console.log('newBookMagazineData', newBookMagazineData)
            )
          }}
          color={'primary'}
          variant={'contained'}
          disabled={!validationSuccess()}
        >
          Add
        </Button>
      </DialogActions>
    </Dialog>
  )
}

export default AddBookMagazineDialog
