import { ADD_NEW_DATA, SET_LIBRARY_DATA } from '../actions/library'
import Immutable from 'immutable'

export const initialState = Immutable.fromJS({
  fullData: [],
  magazines: [],
  books: []
})

export default (state = initialState, action) => {
  switch (action.type) {
    case SET_LIBRARY_DATA:
      return {
        ...state, ...action.payload
      }
    case ADD_NEW_DATA:
      console.log('ADD_NEW_DATA reducer: ', action.payload)
      const newState = { ...state }
      newState.fullData.push(action.payload.newFullDataItem)
      newState[action.payload.newAddition.typedata].push(action.payload.newAddition.data)
      return {
        ...state, ...newState
      }
    default:
      return state
  }
}
