export const SET_LIBRARY_DATA = 'SET_LIBRARY_DATA';

export function setLibraryData (data) {
  let payload = [].concat(data.books, data.magazines).map((item) => {
    {item.authors.split(',').map((authorMail) => {
      let currentAuthorData = {};
      for (let i = 0; i < data.authors.length; i++) {
        if (data.authors[i].email === authorMail) {
          currentAuthorData = data.authors[i];
          break;
        }
      }
      return currentAuthorData;
    })}
  });
  return {
    type: SET_LIBRARY_DATA,
    payload
  }
}
