import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux'
import { tableCells } from './config';
import { Tooltip, TextField, Button } from '@material-ui/core';
import ArrowUpwardIcon from '@material-ui/icons/ArrowUpward';
import ArrowDownwardIcon from '@material-ui/icons/ArrowDownward';
import { orderBy } from 'lodash';
import AddBookMagazineDialog from './AddBookMagazineDialog'
import ExportDataDialog from './ExportDataDialog'


const LibraryTable = () => {

  const libraryFullData = useSelector(state => (state.library?.fullData));
  const [libraryData, setLibraryData] = useState([]);
  const [sortParameters, setSortParameters] = useState({field: 'title', order: 'asc'});
  const [filterValue, setFilterValue] = useState('');
  const [openAddBookMagazineDialogState, setOpenAddBookMagazineDialogState] = useState(false);
  const [openExportDataDialogState, setOpenExportDataDialogState] = useState(false);

  useEffect(() => {
    let libraryFilteredData = !filterValue ? libraryFullData : libraryFullData.filter((item) => {
      if (item.isbn.toString().toLowerCase().includes(filterValue.toLowerCase())) {
        return true
      }
      let authorsMails = '';
      item.authors.forEach(author => {authorsMails += `${author.email} `});
      return authorsMails.toLowerCase().includes(filterValue.toLowerCase());
    });

    libraryFilteredData && setLibraryData(orderBy(libraryFilteredData, sortParameters.field, sortParameters.order));
  }, [libraryFullData?.length, sortParameters, filterValue]);

  const closeAddBookMagazineDialog = () => {
    setOpenAddBookMagazineDialogState(false);
  }
  const openAddBookMagazineDialog = () => {
    setOpenAddBookMagazineDialogState(true);
  }

  const closeExportDataDialog = () => {
    setOpenExportDataDialogState(false);
  }
  const openExportDataDialog = () => {
    setOpenExportDataDialogState(true);
  }

  const makeFilterInput = () => (
    <TextField
      onChange={(e) => {
        setFilterValue(e.target.value);
      }}
      value={filterValue}
      placeholder={'Filter: isbn / author email'}
      style={{margin: '8px', width: '400px'}}
    />
  );

  const makeTableHeader = () => (
    <div style={{width: '100%', borderBottom: '1px solid #e0e0e0', padding: '8px 0'}}>
      {tableCells.map((item, headerCellIndex) => (
        <div
          style={{...item.cellStyle, cursor: item.isSortable? 'pointer' : 'auto'}}
          key={`library-table-header-${headerCellIndex}`}
          onClick={(e) => {
            e.stopPropagation();
            item.isSortable && setSortParameters({field: item.title, order: sortParameters.order === 'asc'? 'desc' : 'asc'});
          }}
        >
          {item.headerTitle}
          <span style={{display: 'inline-block', position: 'absolute', right: '2px', bottom: '-6px'}}>
            {sortParameters.field === item.title ? (
              sortParameters.order === 'asc' ? <ArrowUpwardIcon /> : <ArrowDownwardIcon />
            ) : ''}
          </span>
        </div>
      ))}
    </div>);

  const makeRowCellForObjectValue = (objectItem, rowIndex) => {
    return (
    objectItem.value ? objectItem.value:
      (<>{objectItem.map((item, index) => (
      <div key={`library-table-row-tooltip-cell-${rowIndex}-4-${index}`}>
        <Tooltip title={item.email} interactive={true}>
          <span>{item.firstname} {item.lastname}</span>
        </Tooltip>
      </div>
    ))}</>)
  )}

  const makeTableRows = () => {
    return (
      <div>
        {libraryData?.map((item, rowIndex) => {
          return (
            <div
              key={`library-table-row-${rowIndex}`}
              style={{width: '100%', borderBottom: '1px solid #e0e0e0 ', display: 'flex', alignItems: 'center', padding: '8px 0'}}
            >
              {tableCells.map((cell, cellIndex) => (
                <div
                  key={`library-table-row-cell-${rowIndex}-${cellIndex}`}
                  style={cell.cellStyle}
                >
                  {typeof item[cell.title] !== 'object' ? item[cell.title] : makeRowCellForObjectValue(item[cell.title], rowIndex)}
                </div>
              ))}
            </div>
          );
        })}
      </div>)
  }

  return (
    <>
      <div style={{position: 'relative'}}>
        {makeFilterInput()}
        <div style={{position: 'absolute', right: '8px', top: '6px'}}>
          <Button
            onClick={openAddBookMagazineDialog}
            variant="contained"
            style={{marginRight: '8px'}}
          >
            Add Book / Magazine
          </Button>
          <Button
            onClick={openExportDataDialog}
            variant="contained"
          >
            Export
          </Button>
        </div>
      </div>
      {makeTableHeader()}
      {makeTableRows()}
      <AddBookMagazineDialog
        openState={openAddBookMagazineDialogState}
        onCloseCallback={closeAddBookMagazineDialog}
      />
      <ExportDataDialog
        openState={openExportDataDialogState}
        onCloseCallback={closeExportDataDialog}
      />
    </>
  );
}

export default LibraryTable;
