import React, { useState } from 'react'
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Switch,
  FormControlLabel,
  Button, TextField
} from '@material-ui/core'
import { useDispatch } from 'react-redux'
import { BOOK, MAGAZINE, addBookMagazineTextFields } from './config'

const AddBookMagazineDialog = ({ openState = false, onCloseCallback }) => {

  const [additionType, setAdditionType] = useState(BOOK)
  const [newBookMagazineData, setNewBookMagazineData] = useState({
    title: '',
    isbn: '',
    authors: '',
    publishedAt: '',
    description: ''
  })

  return (
    <Dialog open={openState} onClose={onCloseCallback}>
      <DialogTitle>Add new</DialogTitle>
      <DialogContent>
        <FormControlLabel
          control={
            <Switch
              onChange={() => {
                const type = additionType === BOOK ? MAGAZINE : BOOK
                setAdditionType(type)
                setNewBookMagazineData({ ...newBookMagazineData, publishedAt: '', description: '' })
              }}
              color='primary'
            />}
          label={additionType}
        />
        <br />
        {addBookMagazineTextFields.map((item, index) => (
          <TextField
            key={`add-book-magazine-text-field-${index}`}
            onChange={(e) => {
              const key = typeof item.key === 'string' ? item.key : item.key[additionType]
              setNewBookMagazineData({ ...newBookMagazineData, [key]: e.target.value })
            }}
            value={newBookMagazineData[item.key]}
            placeholder={
              typeof item.placeholder === 'string' ? item.placeholder : item.placeholder[additionType]
            }
            style={{ width: '400px', marginBottom: '8px' }}
          />
        ))}
      </DialogContent>
      <DialogActions>
        <Button
          onClick={onCloseCallback}
        >
          Cancel
        </Button>
        <Button
          onClick={() => { console.log('newBookMagazineData', newBookMagazineData) }}
          color={'primary'}
          variant={'contained'}
        >
          Add
        </Button>
      </DialogActions>
    </Dialog>
  )
}

export default AddBookMagazineDialog
