import React, { useState } from 'react'
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  DialogContentText,
  Switch,
  FormControlLabel,
  Button
} from '@material-ui/core'
import { useDispatch } from 'react-redux'
import { BOOK, MAGAZINE } from './config'

const AddBookMagazineDialog = ({ openState = false, onCloseCallback }) => {

  const [additionType, setAdditionType] = useState(BOOK)

  return (
    <Dialog open={openState} onClose={onCloseCallback}>
      <DialogTitle>Add new</DialogTitle>
      <DialogContent>
        <DialogContentText>
          <FormControlLabel
            control={
              <Switch
                onChange={() => {
                  const type = additionType === BOOK ? MAGAZINE : BOOK
                  setAdditionType(type)
                }}
                color='primary'
              />}
            label={additionType}
          />
        </DialogContentText>
      </DialogContent>
      <DialogActions>
        <Button
          onClick={onCloseCallback}
        >
          Cancel
        </Button>
        <Button
          onClick={() => { console.log('jhjgjh') }}
          color={'primary'}
          variant={'contained'}
        >
          Add
        </Button>
      </DialogActions>
    </Dialog>
  )
}

export default AddBookMagazineDialog
