import React, { useState } from 'react'
import { Dialog, DialogTitle, DialogContent, DialogActions, Switch, FormControlLabel } from '@material-ui/core'
import Button from '@material-ui/core/Button'
import { useSelector } from 'react-redux'
import { CSVLink } from "react-csv";

const ExportDataDialog = ({ openState = false, onCloseCallback }) => {

  const BOOKS = 'BOOKS';
  const MAGAZINES = 'MAGAZINES'

  const libraryFullData = useSelector(state => (state.library?.fullData))
  // eslint-disable-next-line no-undef
  const [exportParams, setExportParams] = useState({ type: BOOKS  })

  return (
    <Dialog open={openState} onClose={onCloseCallback}>
      <DialogTitle>Export</DialogTitle>
      <DialogContent>
        <div>
          <FormControlLabel
            control={
              <Switch
                onChange={() => {
                  const type = exportParams.type === BOOKS ? MAGAZINES : BOOKS
                  setExportParams({ type })
                }}
                color="primary"
              />}
            label={exportParams.type}
          />
        </div>
      </DialogContent>
      <DialogActions>
        <Button
          onClick={onCloseCallback}
        >
          Cancel
        </Button>
        <Button
          onClick={() => { console.log('jhjgjh') }}
          color={'primary'}
          variant={'contained'}
        >
          <CSVLink
            data={[{a: 1, b: '2'}, {a: 2, b: 'asdasd'}]}
            filename={'asdasd.csv'}
          >
            Download me
          </CSVLink>
        </Button>
      </DialogActions>
    </Dialog>
  )
}

export default ExportDataDialog
