import React, { useState } from 'react'
import { Dialog, DialogTitle, DialogContent, DialogActions, Switch, FormControlLabel } from '@material-ui/core'
import Button from '@material-ui/core/Button'
import { useSelector } from 'react-redux'
import { CSVLink } from "react-csv"

const ExportDataDialog = ({ openState = false, onCloseCallback }) => {

  const BOOKS = 'BOOKS'
  const MAGAZINES = 'MAGAZINES'

  const libraryData = useSelector(state => (state.library))
  console.log('libraryFullData', libraryData);
  const [exportDataType, setExportDataType] = useState(BOOKS)

  const getDataForExport = () => (
    exportDataType === BOOKS ? libraryData.books : libraryData.magazines || []
  )

  return (
    <Dialog open={openState} onClose={onCloseCallback}>
      <DialogTitle>Export</DialogTitle>
      <DialogContent>
        <div>
          <FormControlLabel
            control={
              <Switch
                onChange={() => {
                  const type = exportDataType === BOOKS ? MAGAZINES : BOOKS
                  setExportDataType(type)
                }}
                color='primary'
              />}
            label={exportDataType}
          />
        </div>
      </DialogContent>
      <DialogActions>
        <Button
          onClick={onCloseCallback}
        >
          Cancel
        </Button>
        <CSVLink
          data={getDataForExport()}
          filename={`new${exportDataType}.csv`}
        >
          <Button
            onClick={() => {}}
            color={'primary'}
            variant={'contained'}
          >
            DOWNLOAD
          </Button>
        </CSVLink>
      </DialogActions>
    </Dialog>
  )
}

export default ExportDataDialog
