import React, { useState } from 'react'
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  DialogContentText,
  Switch,
  FormControlLabel,
  Button, TextField
} from '@material-ui/core'
import { useDispatch } from 'react-redux'
import { BOOK, MAGAZINE } from './config'

const AddBookMagazineDialog = ({ openState = false, onCloseCallback }) => {

  const [additionType, setAdditionType] = useState(BOOK)
  const [newBookMagazineData, setNewBookMagazineData] = useState({
    title: '',
    isbn: '',
    authors: '',
    publishedAt: '',
    description: ''
  })

  return (
    <Dialog open={openState} onClose={onCloseCallback}>
      <DialogTitle>Add new</DialogTitle>
      <DialogContent>
        <DialogContentText>
          <FormControlLabel
            control={
              <Switch
                onChange={() => {
                  const type = additionType === BOOK ? MAGAZINE : BOOK
                  setAdditionType(type)
                }}
                color='primary'
              />}
            label={additionType}
          />
          <br />
          <TextField
            onChange={(e) => {
              setNewBookMagazineData({ ...newBookMagazineData, title: e.target.value });
            }}
            value={newBookMagazineData.title}
            placeholder={'Title'}
            style={{ margin: '8px', width: '400px' }}
          />
          <TextField
            onChange={(e) => {
              setNewBookMagazineData({ ...newBookMagazineData, isbn: e.target.value });
            }}
            value={newBookMagazineData.title}
            placeholder={'ISBN'}
            style={{ margin: '8px', width: '400px' }}
          />
        </DialogContentText>
      </DialogContent>
      <DialogActions>
        <Button
          onClick={onCloseCallback}
        >
          Cancel
        </Button>
        <Button
          onClick={() => { console.log('newBookMagazineData', newBookMagazineData) }}
          color={'primary'}
          variant={'contained'}
        >
          Add
        </Button>
      </DialogActions>
    </Dialog>
  )
}

export default AddBookMagazineDialog
