import React, { useEffect, useState } from 'react';
import { useStore } from 'react-redux';
import { tableCells } from './config';


const LibraryTable = () => {

  const store = useStore();
  const [libraryData, setLibraryData] = useState([]);

  useEffect(() => {
    console.log('store', store.getState().library);
    setLibraryData(store.getState().library?.fullData);
  }, [store.getState().library?.fullData]);

  const makeTableHeader = () => (
    <div>
      <div style={{display: 'inline-block', width: '10%'}}>
        kj
      </div>
    </div>);

  const makeRowCellForObjectValue = (objectItem) => (
    objectItem.value ? objectItem.value : (<>{objectItem.map(item => (
      <div>{item.email}</div>
    ))}</>)
  );

  const makeTableRows = () => {
    return (
      <div>
        {libraryData?.map((item, index) => {
          return (
            <div key={`library-table-row-${index}`} style={{width: '100%', borderBottom: '1px solid', display: 'flex', alignItems: 'center', padding: '8px 0'}}>
              {tableCells.map((cell, cellIndex) => (
                <div
                  key={`library-table-row-cell-${cellIndex}`}
                  style={{
                    display: 'inline-block',
                    padding: '0 8px',
                    width: cell.widthDefault
                  }}
                >
                  {typeof item[cell.title] !== 'object' ? item[cell.title] : makeRowCellForObjectValue(item[cell.title])}
                </div>
              ))}
              {/*<div style={{display: 'inline-block', width: '45%'}}>*/}
              {/*  {item.additionalData.value}*/}
              {/*</div>*/}
              {/*{makeAuthorsCell(item.authors)}*/}
            </div>
          );
        })}
      </div>)
  }

  return (
    <div>
      Library
      {makeTableHeader()}
      {makeTableRows()}
    </div>
  );
}

export default LibraryTable;
